INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-01-10', NULL, 'At The End of The Line', 'The Exchange, Penzance TR18 2NL', '', '', '7.30pm', '', '', 'At The End of The Line : A concert by The 50 Degrees choir and Emily Dobson.</p>\r\n<p>\r\nThe 50 Degrees Choir has devised original songs, inspired in part from the ideas submitted to a Postcard To Penzance to create an entertaining, questioning and brutally honest look at the place where we live and the reasons that we live here.\r\nThe choir, in a dialogue with dancer Emily Dobson, weave a beautiful story of the hardships and joys we face and the natural world we inhabit, â€œat the end of the lineâ€ in the far west of Cornwall.', 'This exciting project has seen members of the choir with musical director Vicky Abbott create original songs for the first time since the choirâ€™s inception over 10 years ago, in an artistic exploration, combining dance, music and words, with Agnieszka Blonska, an accomplished theatre director.\r\nDon''t miss this opportunity to be the first to hear this truly inspiring and highly entertaining body of work.\r\nDoors and bar open from 7pm, performance at 7.30pm</p>\r\n<p>\r\nÂ£5, tickets available from Space on Arcade Steps, No 36 in Causewayhead, and Pure Nuff Stuff in Chapel Street, all in Penzance.', NULL, '', '');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-03-19', NULL, 'Cornwall Business Show', 'Royal Cornwall Showground', 'http://www.cornwallbusinessshow.co.uk', 'Cornwall business show 2015', '10am to 6pm', '', '', 'The Cornwall Business Show is the biggest, most inclusive business exhibition in Cornwall, bringing together a large spectrum of industries and sectors, from small, start-up businesses to large, established companies.', 'Taking place in the heart of Cornwall at the Royal Cornwall Showground, this years show is bigger than ever, bringing together Cornwallâ€™s most dynamic and ambitious businesses, all under one roof.', NULL, '', '');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-04-25', NULL, 'Camborne Trevithick Day', 'Camborne Town', '', '', 'All day', '', '', 'Camborne''s Trevithick Day is a free one day festival held on the streets of Camborne to celebrate the industrial heritage of the wider area.', 'The day revolves around two parades, dancing in the morning and the steam engines in the afternoon. The festival is made complete by the street entertainment, stalls, exhibitions around the town''s centre. Highlight of the day............. goin'' up Camborne Hill', NULL, 'http://www.westcornwallevents.co.uk/images/festivals/landing_page/trevithic_day_camborne.jpg', 'Camborne Trevithick Day 2015');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-05-08', NULL, 'Helston Flora Day', 'Helson Town', '', '', '', 'http://www.westcornwallevents.co.uk/festival/helston_flora_day.php', 'Helston Flora Day', 'Colourful dancing through the streets. Helston Flora Day takes place on the 8th of May starting at 07.00am.', 'The Hal-An-Tow (a story and dance by costumed performers) takes place at 8.30am. A Children''s Dance at 9am.The streets and houses of Helston are specially decorated for the day.', NULL, 'http://www.westcornwallevents.co.uk/images/festivals/2010_helston_flora_day.jpg', 'Helston Flora Day 2015');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-06-27', NULL, 'Mazey Day 2015', 'Penzance', '', '', 'All day', 'http://www.westcornwallevents.co.uk/festival/penzance_mazey_day_golowan.php', 'May Day Information', 'Penzance comes to life in full technicolor for <a href="../festival/penzance_mazey_day_golowan.php">Mazey Day</a>, the jewel in the <a href="../festival/penzance_mazey_day_golowan.php" title="golowan celebrations summer">Golowan Festival''s</a> crown.</p>\r\n', '<p>The town centre plays host to numerous processions starting at 10am sharp. With numerous parades, loads of live music, dance and theatre performances and hundreds of stalls, you will wonder where the day went.', NULL, 'http://www.westcornwallevents.co.uk/images/festivals/mazy_day_post.JPG', 'Market Jew Street on Masey Day');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-06-26', NULL, 'Mazey Eve Fairground and Fireworks', 'Penzance prom & harbour', '', '', '11.15pm', '', '', 'Mazey Eve plays host to an evening of fun including a fairground, fireworks and Penzance''s very own Penglaz!', 'Fireworks fire at 11.15 pm (or drekly after) and are followed by an informal procession, lead by Penglaz around the wider harbour area.', NULL, 'http://www.westcornwallevents.co.uk/images/2013/golowan_festival_fair_ground.jpg', 'Fireworks on Prnzance prom');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-06-19', '2015-06-28', 'The Golowan Festival 2015', 'Penzance', '', '', '', 'http://www.westcornwallevents.co.uk/festival/penzance_mazey_day_golowan.php', 'Golowan Festival 2015 Information', '<a href="../festival/penzance_mazey_day_golowan.php" title="golowan festival penglaz fair">The Golowan festival</a> is the festival of St John, held in Penzance each year in late June.</p>\r\n', '<p>Events include a <a href="../festival/firework_displays.php">fireworks display</a> on the prom, spectacular parades through the streets and a host of street entertainment. There is plenty of live music and theatre performances to keep you entertained throughout the week.', NULL, 'http://www.westcornwallevents.co.uk/images/festivals/golowan_festival_jacob_cockle.jpg', 'Golowan Flags on Penzance Prom');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-07-13', NULL, 'Stithians Show 2015', 'The Showground, Stithians, Cornwall, TR3 7DP', '', '', '', '', '', 'One of the largest & oldest one day Agricultural Shows in the S W England; friendly and local atmosphere. Free parking.', 'Varied musical entertainment, cage birds, cattle, dogs and dog agility, goats, horses (jumping, etc), pigeons, poultry, rabbits, rare breeds and sheep. Cornwall Federation of Womenâ€™s Institutes and the Cornwall YFC. Large Marquees for Horticulture, Taste of Cornwall Marquee and Domestic Marquee (craft, art, sewing and photo comps, cookery, wine and jam). Countryside Area. Craft Stalls and Trade Stands. Fairground & steam/vintage vehicles displayed. Banking facilities.', NULL, '', '');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-07-18', NULL, 'Lafrowda Day 2015', 'St. Just', '', '', '', 'http://www.westcornwallevents.co.uk/festival/st_just_lafrowda_festival.php', 'Lafrowda Day and Festival', 'The roads are closed, the  decorations go up and St Just gets into party   mood for <a href="../festival/st_just_lafrowda_festival.php" title="St Just Lafrowda Festival">Lafrowda Day</a>.</p>\r\n', '<p> There''s live music, street entertainers and   three magnificent  processions. The children at noon, big images in the   afternoon and  lanterns after dark. The highlight of the week-long   community arts  festival, the day is showcase for the Work of the   Lafrowda Trust.</p>\r\n', NULL, 'http://www.westcornwallevents.co.uk/images/festivals/Lafrowda_Festival_St_Just.jpg', 'Lafrowda day parade');
