INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-09-12', '2015-09-26', 'St Ives September Festival 2015', 'St. Ives', '', '', '', 'http://www.westcornwallevents.co.uk/festival/st_ives_september_festival.php', 'St. Ives September festival information', 'Each year, the St. Ives September Festival celebrates the town''s artistic heritage with a festival of Music and the Arts. The festival runs for two weeks during September with a huge variety of musical performances: jazz, blues, folk sessions, classical concerts. The visual arts are represented well with gallery exhibitions ''open days'', talks, lectures, poetry readings and workshops', '', NULL, '', '');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-12-21', NULL, 'The Montol Festival', 'Penzance', '', '', '', 'http://www.westcornwallevents.co.uk/festival/montol_festival_penzance.php', 'Montol Festival 2015 Information', 'The Montol Festival is an annual arts and community festival in Penzance, Cornwall held between the 14th and 22nd of December each year.', 'The festival is a revival and reinterpretation of many of the traditional Cornish midwinter traditions & Christmas traditions formerly practiced in and around the Penzance area.', NULL, '', '');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-01-17', NULL, 'The Modern Lens: International Photography', 'Tate St Ives, Porthmeor, TR26 1TG', '', '', '', '', '', 'Pioneering artists from across Europe, the Americas and Japan will be shown at Tate St Ives for the first time in The Modern Lens. This will be the largest display of photographic works ever to be exhibited at the gallery.', 'Looking at developments in international photography from the 1920s to the 1960s, the exhibition uncovers the sense of curiosity and experimentation as artists harnessed the medium in new ways.', NULL, '', '');
