INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-01-14', NULL, 'Dr Livingstone I Presume', 'St Ives Guildhall, TR26 2DS', '', '', '7.30pm', '', '', 'This winter Miracle deftly combines its own inimitable style with the wonder of Victorian Music Hall to tell the story of one manâ€™s obsession to discover the source of the Nile and transform an entire continent singlehanded â€“ AND of a woman left behindâ€¦', 'Both a hurrah to courage and endurance and a swipe at the great 19th century ideals of imperialism, chauvinism and hypocrisy, this curious slice of history is served up in a gloriously haphazzard  spectacle!', NULL, 'http://www.visitcornwall.com/sites/default/files/styles/product_image/public/product_image/DR%20L%20promotional%20web_0.jpg?itok=dzU6j8kO', 'Miracle theatre cornwall');
