INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-11-25', '2015-11-28', 'Brassed Off - Cornish Premiere', 'Regal Theatre, Redruth', '', '', '7.30pm', '', '', 'In 1992 the coal mine in a northern English village is threatened with closure, which would also mean the end of the miners brass band. Based on a true story and the 1996 film, Brassed Off is a comedy drama that celebrates the power of music and the capacity of the human spirit to inspire a community to triumph against all the odds. RAOST and Camborne Town Band join forces to portray a story that is funny, heart-warming and inspiring.', '', NULL, 'http://www.merlincinemas.co.uk/image/1714/xbrassed-off-film-pic.png.pagespeed.ic.NrJLtoNAhD.jpg', 'Brassed off');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-02-05', NULL, 'Information Event - Cornwall College Camborne', 'Cornwall College Camborne, Redruth, TR15 3RD', 'http://www.cornwall.ac.uk/events/information-event-camborne-0', 'Cornwall college open evening', '5pm to 7.30pm', '', '', 'Our Information Events are your chance to find out why there\\''s more for you at Cornwall College:\r\n\r\nTalk to tutors about our wide range of courses\r\nTour the campus and see our great facilities\r\nMeet current students and find out about student life\r\nFind out about transport, finance and student support', 'At Duchy College, we offer the widest range of courses and qualifications: </p><p>\r\nDiplomas<br/>\r\nPractical Skills Training<br/>\r\nApprenticeships<br/>\r\nFoundation Degrees<br/>\r\nHonours Degrees<br/>\r\nProfessional Qualifications<br/>\r\nPostgraduate Study<br/>', NULL, '', '');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-04-30', NULL, 'Derek Acorah Famous Spirit Medium', 'Penventon Hotel, Redruth.', '', '', '7pm', '', '', 'Derek Acorah needs no introduction. The pioneer for Spiritualism and mediumship on television, he is arguably the world\\''s most renowned Spirit Medium having demonstrated to many hundreds of thousands of people in theatres throughout the UK.', '', NULL, 'http://imageprocessor.websimages.com/width/300/crop/0,0,300x452/hauntingexperiences.webs.com/Derek1%202014.jpg', 'Derek acorah');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-02-09', NULL, 'St Ives Feast - Hurling the Silver Ball', 'St. Ives', '', '', 'All day', '', '', 'The St Ives Feast is an ancient tradition that celebrates the anniversary of the consecration of the Parish Church of St Eia in 1434 AD and offers a rare chance to watch the game of Hurling the Silver Ball, a centuries old form of rugby.', 'The feast day falls on February 9 and starts off with the mayorâ€™s civic procession for the blessing at St Ia Well near Porthmeor Beach followed by the start of the boisterous hurling of the silver ball when participants attempt to win the ball off each other around the town. Whoever returns the ball to the mayor on the steps of St Ives Guildhall on the stroke of midday receives a silver coin. In the afternoon, pennies are proffered from the balcony by town councillors to the waiting children on the Guildhall forecourt.', NULL, '', '');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-01-24', NULL, 'Falmouth Soul Sensation - Benefit Gig', 'Acorn Penzance', 'http://theacornpenzance.com/whats-on/music/falmouth-soul-sensation/', 'falmouth soul sensation', '8pm', '', '', 'The Falmouth Soul Sensation are returning to The Acorn after their highly successful Penzance debut there last January. This year their benefit gig is in aid of the Jubilee Pool restoration fund.', 'They are an exciting 12-piece local danceband, playing the best of classic 60â€™s soul music from Booker T and Aretha to Sam and Dave, Otis and Wilson Pickett. Keeping alive the tradition of good Soul Music, from North and South, the band regularly plays to packed audiences around Falmouth.', NULL, 'http://theacornpenzance.com/wp-content/uploads/Falmouth-Soul-web-1024x394.jpg', 'Falmouth soul sensation');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-01-30', NULL, 'Dracula', 'Acorn Penzance', 'http://theacornpenzance.com/whats-on/theatre-comedy/dracula/', 'Dracula theatre show', '8pm', '', '', 'The team that brought you the award-winning â€˜Great Expectationsâ€™ have got their teeth into Bram Stokerâ€™s classic tale of the vampire.', 'Blood, bats and sexy lady vampires. A madman eating flies. Coffins, crucifixes, crypts and the Count! Itâ€™s all here in this new adaptation of Bram Stokerâ€™s gothic pot-boiler!\r\n\r\nBe warned: this monstrous, disturbing and completely batty tale of dark deeds and the Un-Dead, will thrill and seduce you!', NULL, 'http://theacornpenzance.com/wp-content/uploads/dracula_web-300x288.jpg', 'Dracula');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-02-28', NULL, 'Martha Tilston', 'Acorn Penzance', 'http://theacornpenzance.com/whats-on/music/martha-tilston/', 'Matha Tilston', '8pm', '', '', 'Martha Tilston has grown up immersed in music. So much so that she thinks of song writing as the coat she wears on the journey, not the destination she is travelling to. It has taken her to many interesting places; opening the acoustic stage at Glastonbury; touring the far reaches of the globe; gaining a BBC nomination for best new-comer at the folk awards; recording and releasing several of her own albums, being a guest vocalist for zero 7, and meeting and playing with some of the worldâ€™s most inspiring musicians.', '', NULL, 'http://theacornpenzance.com/wp-content/uploads/Martha-Tilston-2015-300x200.jpg', 'Martha Tilston');
