INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-03-06', NULL, 'The Devil with the Three Golden Hairs', 'Acorn Penzance', 'http://theacornpenzance.com/whats-on/theatre-comedy/the-devil-with-three-golden-hairs/', 'Devil with three golden hairs', '8pm', '', '', 'The shadow-puppetry of Devon-based arts duo The Clockwork Moth has mesmerised audiences of all ages across the South-west. Now they bring a hidden gem by the Brothers Grimm to the stage. â€˜The Devil with the Three Golden Hairs,â€™ blends intricate shadow-puppetry, storytelling and live music to create a sensory feast.', 'The tale is classic Grimmâ€™s â€“ darkly sweet, funny, and deeply magical â€“ A brutal King, a deathly ferryman, a narcissistic Lucifer, and a host of wise-women, curses, enchantments and courtly delights. This is the tale of a teenage boy, believed to have been born lucky, who embarks on a journey to the underworld and back.', NULL, 'http://theacornpenzance.com/wp-content/uploads/Poster-for-web-243x300.jpg', 'Devil with three golden hairs');
