INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-04-16', NULL, 'Keith James : The Songs of Nick Drake', 'Acorn Penzance', '', '', '8pm', '', '', 'Nick Drake is the most revered and truly loved of all of this countryâ€™s singer/songwriters. He recorded three cherished albums between 1969 and 1972 but was almost entirely unknown in his day.', 'This new concert will have, as far as can be possible, a fresh and rewarding set list and will include an interpretation of a Molly Drake composition, a transcription of a poem by William Blake (Nickâ€™s greatest influence) and a song by Sixto Rodriguez â€“ an unusually similar songwriter from the exact same era, but from a parallel universe.', NULL, 'http://theacornpenzance.com/wp-content/uploads/NICK-DRAKE-BLANKET-Web.gif', 'Keith James');
