INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-04-05', '2015-04-06', 'TÃ‚N FEST 2015', 'Wendron Football Club, Underlane, TR13 0EH', '', '', '10 am to 5 pm', 'http://www.swampcircus.co.uk/tan-fest/', 'Swamp Circus', 'Looking for something to keep the children entertained this Easter Holiday?\r\n\r\nThen look no further, Tan Festival has it all. Bouncy castles, rides, live bands, and creative workshops are just some of the things on offer at this two day festival brought to you by Swamp Circus Trust.', 'Alongside all these great festivities, students from Swamp Circusâ€™ training school will be putting on a not to be missed Big Top cabaret performance.\r\n\r\nTan Fest will be held at Wendron Football Club, Helston on Sunday 5th April and Monday 6th April. Doors open at 10am - 5pm. Wristbands are only Â£3.00 per day and can be purchased on the door.', NULL, '', '');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-05-28', '2015-06-01', 'Acrobatic Camp Cornwall 2015', 'Chyan Farm, Halvasso, nr Penryn, Cornwall, TR109BT', 'http://www.swampcircus.co.uk/acrobatic-camp/', 'Swamp Circus', '9am to 9pm', '', '', 'BIG TOP, CAMPING, TOP TUTORS, FOCUSED TRAINING & GREAT FOOD!\r\n\r\nA rare opportunity to focus on your acrobatic skills this summer in the beautiful Cornish Countryside!\r\n\r\nGet one to one tuition from experts. Activities include acro-balance, trampolining, aerial acrobatics, yoga and tumbling.\r\n\r\nBe entertained in our big top, gorge yourself on local organic delicacies, relax and unwind in our solar showers and saunas.', '', NULL, '', '');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-11-29', NULL, 'Yuletide Fair', 'St Just Sports Centre, Cape Cornwall road, St Just TR19 7JX', '', '', '11 am to 3 pm', '', '', 'After a year off we have changed our name and moved to a larger venue to showcase even more of our locally made fantastic gifts and foods.\r\n\r\nWe are a non profit making group who want to give a little extra to our local children.', 'The choirs from St.Just and Pendeen Schools will come and sing carols and Father Christmas will be there.\r\n\r\nThe Sports hall will provide refreshments including mulled wine and mince pies.\r\nIf you would like a stall please email ruth_bosgwyn@yahoo.co.uk', NULL, '', '');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-05-17', NULL, 'Race for Life', 'Gyllyngvase Beach Car Park, Falmouth, Cornwall, TR11 4PA', 'http://bit.ly/RFLSWest', 'Race for Life South West', '11 am', '', '', 'Cancer Research UK\\''s Race For Life 2015 needs women to take part and raise money to help them continue their life-saving work. In the UK it is estimated that every two minutes, a woman is told they have cancer. This is why we need you. We need you to take part and raise money to help continue the life-saving work.', 'Ladies, itâ€™s time get our fighting mentality out. The reality is that it will affect one in three of us. It will cruise into our lives and wreck everything with no regard. We will not let it rule us. Cancer is going down. Thatâ€™s why we, Race for Life, take pride in reminding cancer that we are coming to get it.', NULL, '', '');
