INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-06-20', NULL, 'Rosie and the Goldbug + Keltrix', 'Ritz Penzance', '', '', '8.30pm', '', '', 'Rosie and the Goldbug are a Cornish indie-rock trio who have been together since 2007. They''re electrifying sound are sure to get you in the festival spirit this year.', 'They''re plating the Ritz Penzance as part of a larger programme of events over the Golwoan Festival, 2015.', NULL, '../images/2015/rosie.jpg', '');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-06-24', NULL, 'Lost Dawn + Velvet Hands', 'Ritz Penzance', '', '', '8:30pm', '', '', 'Lost dawn are a thrilling trio who meld raw blues, killer rock riffs, the energy of punk and the idiosyncrasies of freak-folk with a grasp of glam dynamics  are one of the best live acts you will see. Anywhere, not just in Cornwall.', '', NULL, '', '');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-06-25', NULL, 'Falmouth Soul Sensation', 'Ritz Penzance', '', '', '8:30pm', '', '', 'Falmouth Soul Sensation consists of a core of experienced musicians who offer other local musicians and singers of all ages and abilities the opportunity to work in a band environment. The result is a vibrant and exciting mix of ages and instruments supported by experienced musicians. We play a diverse mix of classic and modern soul music that appeals to all ages', 'The Soul Sensation played to a rapturous response at the Looe Music Festival and were immediately booked back again for the following year, and this year played to a sell out crowd at the Acorn in Penzance raising money for the Save the Lido Campaign to restore the iconic, art deco Lido on The Penzance waterfront. The band also supported the Three Degrees at the Hall For Cornwall on the West Country leg of thier tour.', NULL, '../images/2015/fss.jpg', 'Falmouth Soul Sensation');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-05-26', NULL, 'Mock Mayor Election & Lost in Space', 'Ritz Penzance', '', '', '8:30pm to 11:00pm', '', '', 'By order of the Golowan Decorum Committee you are hereby summoned to attend the annual election for the MOCK MAYOR OF THE QUAY this MAZEY EVE. Headlined by the excellent  Lost in Space. A madcap mix of funky ska, retro grooves and gypsy stomp, from full fat brass to hot strings, seven piece band that kicks out blistering sounds to shake that ass&quot; repeating their Cornish Celtic Ceilidh set commissioned for the brilliant Lowender Peran festival this year taking place in Newquay. Dances on the evening called by the equally excellent Ellie Allen!', 'Followed by the election which will be conducted by the ANCIENT METHOD restored to the heart of the festival. Candidates. Max capacity 500 people!', NULL, '', '');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-06-28', NULL, 'Kabasa', 'Ritz Penzance', '', '', '8:30pm - 11:45pm', '', '', 'From pre-revolution Havana, to the hip beaches of Rio, to 70\\''s New York and the awesome Fania label, Kabasa! has collected some of the finest Latin music (from Cuban Son & vintage NY Salsa, to Cumbia, Afrobeat & Samba...) and given it our own shot of rum and twist of lime. Our unique arrangements fuse elements of Latin Funk & Jazz with influences from Spain, Trinidad, Jamaica, Africa, North & South Brazil, Columbia and of course Cuba!', '', NULL, '', '');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-07-11', '2015-05-12', 'S.W.A.T', 'Ritz Penzance', '', '', '9pm', '', '', 'SWAT DJs present another night of amazing dance music served up by some of the best talent in the SW. ', 'Another night not to miss....', NULL, '', '');
INSERT INTO `events` (`date`, `dateend`, `title`, `location`, `link`, `linkTitle`, `time`, `more_link`, `moreTitle`, `description`, `description2`, `description3`, `image_link`, `imageAlt`) VALUES('2015-07-18', NULL, 'Elegy', 'Ritz Penzance', '', '', '9:00pm', '', '', 'After a little break we will be back at the Ritz to Kick off Summer for you. You know what to expect! House / DNB / Whatever else we fancy. See you there.', '', NULL, '', '../images/2015/elegy.jpg');
