  {date: "2015-04-25", dateend: nil, title: "Camborne Trevithick Day", location: "Camborne Town", link: "", linktitle: "", time: "All day", more_link: "", moreTitle: "", description: "Camborne's Trevithick Day is a free one day festival held on the streets of Camborne to celebrate the industrial heritage of the wider area.", description2: "The day revolves around two parades, dancing in the morning and the steam engines in the afternoon. The festival is made complete by the street entertainment, stalls, exhibitions around the town's centre. Highlight of the day............. goin' up Camborn", description3: nil, image_link: "http://www.westcornwallevents.co.uk/images/festivals/landing_page/trevithic_day_camborne.jpg", imageAlt: "Camborne Trevithick Day 2015", festival_id: nil, venue_id: nil},
  {date: "2015-05-08", dateend: "2015-06-15", title: "Helston Flora Day", location: "Helson Town", link: "", linktitle: "", time: "", more_link: "http://www.westcornwallevents.co.uk/festival/helston_flora_day.php", moreTitle: "Helston Flora Day", description: "Colourful dancing through the streets. Helston Flora Day takes place on the 8th of May starting at 07.00am.", description2: "The Hal-An-Tow (a story and dance by costumed performers) takes place at 8.30am. A Children's Dance at 9am.The streets and houses of Helston are specially decorated for the day.", description3: "", image_link: "http://www.westcornwallevents.co.uk/images/festivals/2010_helston_flora_day.jpg", imageAlt: "Helston Flora Day 2015", festival_id: 2, venue_id: nil},
  {date: "2015-06-27", dateend: "2015-06-15", title: "Mazey Day 2015", location: "Penzance", link: "", linktitle: "", time: "All day", more_link: "http://www.westcornwallevents.co.uk/festival/penzance_mazey_day_golowan.php", moreTitle: "May Day Information", description: "Penzance comes to life in full technicolor for <a href=\"../festival/penzance_mazey_day_golowan.php\">Mazey Day</a>, the jewel in the <a href=\"../festival/penzance_mazey_day_golowan.php\" title=\"golowan celebrations summer\">Golowan Festival's</a> crown.</p>", description2: "<p>The town centre plays host to numerous processions starting at 10am sharp. With numerous parades, loads of live music, dance and theatre performances and hundreds of stalls, you will wonder where the day went.", description3: "", image_link: "http://www.westcornwallevents.co.uk/images/festivals/mazy_day_post.JPG", imageAlt: "Market Jew Street on Mazey Day", festival_id: 1, venue_id: nil},
